mapper.select:
SELECT lmf.user_id,lmf.source_id,ll.language_name,lc.course_name,lv.level_name, lch.chapter_name,lch.thumbnail_url,lch.chapter_intro,lch.id chapterId,lc.id courseId FROM lt_my_favorite AS lmf left JOIN `lt_chapter` AS lch ON lmf.source_id=lch.id left JOIN `lt_level` AS lv ON lch.level_id=lv.id left JOIN `lt_course` AS lc ON lv.course_id=lc.id left JOIN `lt_language` AS ll ON lc.language_id=ll.id
lmf.user_id=#{userId} AND lmf.source=#{source}
ORDER BY lmf.create_time DESC

SELECT mf.user_id,mf.source,mf.source_id,dis.title,dis.thumbnail, dis.video,dis.content,dis.`type`,dis.activitytime,dis.ticket,dis.skip,dis.videoImg FROM `lt_my_favorite` AS mf LEFT JOIN lt_discover AS dis ON dis.id=mf.source_id
mf.user_id=#{userId} AND mf.source=#{source}
ORDER BY mf.create_time DESC

SELECT mf.user_id,mf.source_id,mf.source, ls.title,ls.describe,ls.video_cover FROM `lt_my_favorite` AS mf LEFT JOIN lt_special AS ls ON ls.id=mf.source_id
mf.user_id=#{userId} AND mf.source=#{source}
ORDER BY mf.create_time DESC

mapper.delete:
DELETE FROM lt_my_favorite
WHERE
user_id=#{userId}
AND source_id=#{sourceId}
AND source=#{source}

mapper.select:
SELECT COUNT(id) FROM lt_my_favorite
WHERE
user_id=#{userId}
AND source_id=#{sourceId}
AND source=#{source}
