mapper.update:
update lt_user
nickname=#{nickname}, password=#{password}, sex=#{sex}, age_group=#{ageGroup}, learning_goal=#{learningGoal}, birthday=#{birthday}, head_portrait_url=#{headPortraitUrl}, occupation=#{occupation}, interest=#{interest}, qq_openid=#{qqOpenid}, wx_openid=#{wxOpenid},
where id=#{id}

UPDATE language_talent.lt_user
    SET
    currency = currency - #{price}
    WHERE
    id = #{id} ;

UPDATE
   lt_user
SET
   currency=currency+#{currency}
WHERE
  id=#{id}

UPDATE
   lt_user
SET
   mobile=#{phone}
WHERE
  id=#{userId}
